DECLARE @company XML = '<Company><Code>gk</Code><Name>GraceKennedy Limited</Name><About>Jamaican company</About><TotalEmployed>200</TotalEmployed><Wiki>https://en.wikipedia.org/wiki/GraceKennedy</Wiki><WebSite>https://www.gracekennedy.com/</WebSite><Founded>1922-02-14T05:00:00Z</Founded><CountryCode>JM</CountryCode><Created>1999-11-04T05:00:00Z</Created><Industries /><Files><FileContent><No>0</No><Type>Logo</Type><TypeInt>0</TypeInt><FileName>gk-logo.png</FileName><Content></Content><ContentSize>13146</ContentSize><ContentType>image/png</ContentType><Created>2022-09-01T19:03:54.2118688-05:00</Created></FileContent></Files></Company>';

SELECT	c.x.query('Code').value('.', 'nvarchar(20)'),
        c.x.query('Name').value('.', 'nvarchar(max)'),
        c.x.query('About').value('.', 'nvarchar(max)'),
        c.x.query('Wiki').value('.', 'nvarchar(2100)'),
        c.x.query('WebSite').value('.', 'nvarchar(2100)'),
        c.x.query('TotalEmployed').value('.', 'int'),
        c.x.query('Founded').value('.', 'date'),
        c.x.query('CountryCode').value('.', 'nvarchar(2)')
FROM    @company.nodes('Company') c ( x );

DECLARE @created DATETIME2 = GETDATE();

DECLARE @tblFiles TABLE
(
        [No] BIGINT,
        [Type] INT NOT NULL,
        [FileName] NVARCHAR(256) NOT NULL,
        [Content] VARBINARY(MAX) NOT NULL,
        [ContentSize] INT NOT NULL,
        [ContentType] NVARCHAR(50) NOT NULL,
        [Created] DATETIME2 NOT NULL
    );

    INSERT INTO @tblFiles
    (
        [Type] ,
        [FileName] ,
        [Content] ,
        [ContentSize] ,
        [ContentType] ,
        [Created]
    )
    SELECT	f.x.query('FileContent/TypeInt').value('.', 'int'),
            f.x.query('FileContent/FileName').value('.', 'nvarchar(256)'),
            f.x.query('FileContent/Content').value('.', 'varbinary(max)'),
            f.x.query('FileContent/ContentSize').value('.', 'int'),
            f.x.query('FileContent/ContentType').value('.', 'nvarchar(50)'),
            @created
    FROM    @company.nodes('Company/Files') f ( x );

select * from @tblFiles;